import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'TestApp';
}
<div class="container-fluid text-white">
  <div class="row">
    <div class="col-md-1 col-sm-0 bg-color-registration-page"></div>
    <div class="col-md-7 bg-color-registration-page  pt-5 col-sm-12 min-vh-100 pb-3">
      <app-event-registration></app-event-registration>
    </div>
    <div class="col-md-4 bg-color-event-details pt-5 px-5  col-sm-12 min-vh-100 pb-3">
      <app-event-details></app-event-details>
    </div>
  </div>
</div>
